import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { TestimoniosService } from 'src/app/services/testimoniosService';


@Component({
  selector: 'app-cards-testimonials',
  templateUrl: './cards-testimonials.component.html',
  styleUrls: ['./cards-testimonials.component.css']
})
export class CardsTestimonialsComponent implements OnInit {
  testimonios: any[] = [];
  constructor(private router: Router, private testimoniosService: TestimoniosService) { }

  ngOnInit() {
    this.getTestimonios()
  }


  getTestimonios() {
    this.testimoniosService.getTestimonios().subscribe(
      res => {
        this.testimonios = res
      }
    )
  }


}
<div class="cards">
  <div class="cards__title">
    <h2 class="cards__title-h2">{{ "TESTIMONIALS.TITLE" | translate }}</h2>
  </div>
  <div class="cards__inferior">
    <div *ngFor="let testimonio of testimonios" class="cards__box">
      <div class="cards__boxs">
        <div class="starDiv">
          <img src="assets/icons/Star.svg" />
          <p class="starDiv-puntaje">{{ testimonio.score }} / 5</p>
        </div>
        <div class="divText">
          <p>{{ testimonio.texts }}</p>
          <h3 class="nombre">{{ testimonio.name }}</h3>
        </div>
      </div>
    </div>
  </div>
</div>
