import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-new-testimonials',
  templateUrl: './new-testimonials.component.html',
  styleUrls: ['./new-testimonials.component.css']
})
export class NewTestimonialsComponent {
  constructor(private router:Router){}


  goContact() {
    this.router.navigate(['contact']);
    window.scrollTo(0, 0);
  }
}
<div class="nuevosTestimonios">
  <div class="nuevosTestimoniosBox">
    <h2 class="nuevosTestimoniosBoxTitulo">{{"NEW_TESTIMONIALS.TITLE" | translate}}</h2>
    <p>
      {{"NEW_TESTIMONIALS.DESCRIPTION" | translate}}
    </p>
    <div class="nuevosTestimoniosBoton" (click)="goContact()">
      {{"NEW_TESTIMONIALS.BUTTON" | translate}}
    </div>
  </div>
  <div class="nuevosTestimoniosBox"></div>
</div>
