import { Component } from '@angular/core';
import { Router } from '@angular/router';
import { LanguageService } from 'src/app/services/language.service';

@Component({
  selector: 'app-nav',
  templateUrl: './nav.component.html',
  styleUrls: ['./nav.component.css']
})
export class NavComponent {

  constructor(private router: Router, private languageService: LanguageService) { }

  active = false;
  enOn = false;
  esOn = true;

  goComunity() {
    this.router.navigate(['/community'])
    this.noShowBurger()
  }

  goHome() {
    this.router.navigate(['/'])
    this.noShowBurger()
  }

  goBussines() {
    this.router.navigate(['forBusiness'])
    this.noShowBurger()
  }
  goAboutUs() {
    this.router.navigate(['aboutUs'])
    this.noShowBurger()
  }
  goContact() {
    this.router.navigate(['contact'])
    this.noShowBurger()
  }

  showBurger() {
    this.active = !this.active;
  }

  noShowBurger() {
    this.active = false;
  }

  changeLanguage(lang: string) {
    this.languageService.setLanguage(lang);
    if (lang === "es") {
      this.esOn = true;
      this.enOn = false;
    } else {
      this.esOn = false;
      this.enOn = true;
    }
  }

}
<div class="nav">
  <img src="assets/icons/title.svg" class="nav__icon" (click)="goHome()" />
  <div class="nav__links" [ngClass]="{ active: active }">
    <h2 class="nav__links-comunidad" (click)="goHome()" *ngIf="active">
      {{ "NAV.HOME" | translate }}
    </h2>
    <h2 class="nav__links-comunidad" (click)="goComunity()">
      {{ "NAV.COMMUNITY" | translate }}
    </h2>
    <h2 class="nav__links-business" (click)="goBussines()">
      {{ "NAV.PAID_VERSION" | translate }}
    </h2>
    <h2 class="nav__links-nosotros" (click)="goAboutUs()">
      {{ "NAV.ABOUT_US" | translate }}
    </h2>
    <h2 class="nav__links-contacto" (click)="goContact()">
      {{ "NAV.CONTACT" | translate }}
    </h2>
    <img
      class="nav__links-lenguaje"
      src="../../../assets/icons/es.png"
      (click)="changeLanguage('es')"
      *ngIf="enOn"
    />
    <img
      class="nav__links-lenguaje"
      src="../../../assets/icons/en.png"
      (click)="changeLanguage('en')"
      *ngIf="esOn"
    />
  </div>
  <div class="nav__burguer" (click)="showBurger()">
    <div class="nav__burguer-line-1" [ngClass]="{ active: active }"></div>
    <div class="nav__burguer-line-2" [ngClass]="{ active: active }"></div>
    <div class="nav__burguer-line-3" [ngClass]="{ active: active }"></div>
  </div>
</div>
